import { 
    FETCH_CLIENTS_START,
    FETCH_CLIENTS_SUCCESS,
    FETCH_CLIENTS_FAILURE,
    SEARCH_CLIENTS
} from 'actionTypes'

import {fetchClients as fetchClientsApi} from 'api'

export const fetchClients = () => async dispatch => {
    dispatch({type: FETCH_CLIENTS_START})
    try {
        const clients = await fetchClientsApi()
        dispatch({type: FETCH_CLIENTS_SUCCESS,payload:clients})
    } catch (error) {
        dispatch({
            type:FETCH_CLIENTS_FAILURE,
            payload: error,
            error: true
          })
    }
}

export const searchClients = (value) => dispatch => {
    dispatch({type: SEARCH_CLIENTS,payload:value})
}