import * as R from 'ramda'

import {FETCH_CLIENTS_SUCCESS} from 'actionTypes'

const initialState = {
    ids: [],
    search: '',
}

export default (state = initialState,{type,payload}) => {
    switch (type) {
        case FETCH_CLIENTS_SUCCESS:
            return R.merge(state,{
                ids:R.pluck('id',payload)
            })
        default:
            return state;
    }
}